<script>

export default {

  methods: {
    change_color_mode() {
      if (this.$colorMode.value === 'dark') {
        this.$colorMode.value = 'light'
        this.$colorMode.preference = 'light'
      } else {
        this.$colorMode.value = 'dark'
        this.$colorMode.preference = 'dark'
      }
    }
  }
}
